val orderItemsByPName = sqlContext.sql("SELECT oi.order_item_order_id, collect_set(p.product_name) products FROM order_items oi join products p ON (oi.order_item_product_id = p.product_id ) group by oi.order_item_order_id order by oi.order_item_order_id")
import org.apache.spark.mllib.fpm.FPGrowth
import scala.collection.mutable._
// ProductCombinations class that holds (string of) products and the associated frequency
class ProductCombinations (var products: String, var freq: Long) extends Ordered [ProductCombinations] {
    override def toString = {
        (products + " bought ***" + freq + "*** time(s)")
    }
    // return 0 if the same; negative if this < that; positive if this > that; used for sorting
    def compare (that: ProductCombinations) = {
        if (this.freq == that.freq) {
            0
        }
        else if (this.freq > that.freq) {
            1
        }
        else {
            -1
        }
    }
}
val orders = orderItemsByPName.rdd.map(r => {
    r.getAs[WrappedArray[Int]](1).mkString(",").split(",")
})
val model = new FPGrowth()
  .setMinSupport(0)
  .run(orders)
var PCList = List[ProductCombinations]()
model.freqItemsets.collect().foreach {
    itemset => {
      var pc = new ProductCombinations(itemset.items.mkString(","), itemset.freq)
      PCList = pc :: PCList
    }
}

// Exclude entries with single products and display top 10 combinations of products bought together
var multipleProductCombos = PCList.filter(p => p.products.split(",").size == 2)
                                  .sortWith(_.freq > _.freq).take(10)

var products = multipleProductCombos.map { p =>
    val splitted = p.products.split(",")
    (splitted(0), splitted(1), p.freq)
}.toDF("Product1", "Product2", "Qty")
val aggregated = products.groupBy("Product1").agg(lit("").alias("Product2"), count("Product2").alias("Count - Qty"), sum("Qty").alias("Sum - Qty"))
val productsRenamed = products.select(products("Product1"), products("Product2"), lit(1).alias("Count - Qty"), products("Qty").alias("Sum - Qty"))
val merged =  aggregated.unionAll(productsRenamed).sort("Product1")
var result = merged.select(expr("case when Product2 = '' then Product1 else '' end").alias("Product1"), merged("Product2"), merged("Count - Qty"), merged("Sum - Qty"))
var total = aggregated.select(lit("Total Results").alias("Product1"), lit("").alias("Product2"), sum("Count - Qty").alias("Count - Qty"), sum("Sum - Qty").alias("Sum - Qty"))
z.show(result.unionAll(total))
